mapper.update:
UPDATE boards
SET
title = #{title},
contents=#{contents}
WHERE
id = #{b_id}

UPDATE boards
SET hit = hit + 1
WHERE id = #{id}

mapper.select:
SELECT count(*)
FROM board_likes
WHERE b_id = #{b_id}
AND user_id = #{user_id}

mapper.delete:
DELETE
FROM board_likes
WHERE b_id = #{b_id}
AND user_id = #{user_id}

mapper.select:
SELECT count(*)
FROM board_likes
WHERE b_id = #{b_id};
