mapper.select:
SELECT *
FROM makgol.stores
WHERE category = #{category}
AND 1000 > ST_DISTANCE_SPHERE(POINT(longitude, latitude), POINT(#{longitude}, #{latitude}));

SELECT *
FROM stores
JOIN menus ON stores.id = menus.store_id
WHERE menus.menu = #{category}
AND 1000 > ST_DISTANCE_SPHERE(POINT(longitude, latitude), POINT(#{longitude}, #{latitude}));

SELECT name, likes, address, category, phone, photo, site
FROM makgol.stores
WHERE id = #{storeId}

SELECT menu, price
FROM makgol.menus
WHERE store_id = #{storeId}

SELECT photo_path
FROM makgol.review_images
WHERE review_id = #{reviewId}

SELECT name, photo_path
FROM makgol.users
WHERE id = #{userId}

SELECT *
FROM makgol.store_likes
WHERE user_id = #{user_id}
  AND store_id = #{shop_id}

mapper.update:
UPDATE makgol.stores
SET likes = likes + 1
WHERE id = #{shop_id}

UPDATE makgol.stores
SET likes = likes - 1
WHERE id = #{shop_id}

mapper.delete:
DELETE
FROM makgol.store_likes
WHERE user_id = #{user_id}
  AND store_id = #{shop_id}
