mapper.update:
UPDATE boards
SET title=#{title},
    contents=#{contents},
    attachment=#{attachment}
WHERE id = #{b_id}

mapper.select:
SELECT count(*)
FROM board_likes
WHERE b_id = #{b_id}
  AND user_id = #{user_id}

mapper.delete:
DELETE FROM board_likes
WHERE b_id = #{b_id}
  AND user_id = #{user_id}

mapper.select:
SELECT count(*)
FROM board_likes
WHERE b_id = #{b_id};

mapper.update:
UPDATE boards
SET sympathy=#{totalLike}
WHERE id = #{b_id}
